import React, { Component } from 'react';

import { table, Button,Form,FormGroup,Label,Input } from 'reactstrap';
export class FormProduct extends Component {
  render() {
    return (
      <Form>
		  <FormGroup>
			<Label for='id'>Id</Label>
			<Input id='id' type='text' placeholder='id'></Input>
		  </FormGroup>
		  <FormGroup>
			<div className='form-row'>
				<div className='col-md-6'>
					<Label for='descipcion'>Descripcion</Label>
					<Input id='descripcion' type='text' placeholder='descripcion de la empresa'></Input>
				</div>
				<div className='col-md-6'>
				{/*	<Label for='descipcion'>Descripcion</Label>
				<Input id='descripcion' type='text' placeholder='descripcion de la empresa'></Input>
				*/}
				</div>
			</div>
		  </FormGroup>
		  <Button color='primary' block>Salvar</Button>
      </Form>
    );
  }
}

export class ListProduct extends Component {

  render() {
	  const {products}=this.props;
    return (
      <table className='table-bordered text-center'>
		  <thead className='thead-dark'>
			  <tr>
				  <th>id</th>
				  <th>Nombre</th>
				  <th>Acción</th>
			  </tr>
		  </thead>
		  <tbody>
		  {
			  products.map(products => (
			  <tr key={products.id}>
				  <td>{products.id}</td>
				  <td>{products.nombre}</td>
				  <td>
					<Button color='info' size='sm'>Editar</Button>
					<Button color='danger' size='sm'>Borrar</Button>
				  </td>
			  </tr>
			  ))
		  }
		  </tbody>

      </table>
    );
  }
}

export default class ProductBox extends Component {
	url='http://localhost:85/produ3/web/app_dev.php/listadoJson/empresa';
	state={
		products:[]
	}
  componentDidMount() {
	  fetch(this.url)
			.then(response => response.json())
			/*.then(products => console.log(products))*/
			.then(products => this.setState({products}))
			.catch(e => console.log(e));
  }
  render() {
    return (
       <div className="row">
		<div className="col-md-6 my-3">
			<h1 className='font-weight-bold text-center'>Listado de Productos</h1>
			<ListProduct products = {this.state.products}/>
		</div>
		<div className="col-md-6 my-3">	
			<h1 className='font-weight-bold text-center'>Detalle de Productos</h1>		
			<FormProduct/>
		</div>	
      </div>
    );
  }
}
